package floats

import (
	"math"
	"testing"
)

func TestNearestIdxForSpan(t *testing.T) {
	tests := []struct {
		n   int
		l   float64
		u   float64
		v   float64
		idx int
	}{
		{5, 0, 10, 5, 2},      // Normal case in the middle
		{5, 0, 10, 0, 0},      // Edge case at the lower bound
		{5, 0, 10, 10, 4},     // Edge case at the upper bound
		{5, 5, 0, 0, 0},       // Reversed bounds
		{5, -10, 10, -5, 2},   // Negative range and value
		{3, 0, 10, math.NaN(), 0}, // v is NaN
		{3, math.NaN(), 10, 5, 2}, // l is NaN
		{3, 0, math.NaN(), 5, 0},  // u is NaN
		{3, math.Inf(-1), math.Inf(1), 0, 0}, // l is -Inf, u is Inf
		{2, 0, 10, 10, 1}, // Exactly two elements
	}

	for _, tt := range tests {
		t.Run("", func(t *testing.T) {
			defer func() {
				if r := recover(); tt.n < 2 && r == nil {
					t.Errorf("expected panic for n=%d", tt.n)
				}
			}()
			result := NearestIdxForSpan(tt.n, tt.l, tt.u, tt.v)
			if result != tt.idx {
				t.Errorf("For NearestIdxForSpan(%d, %f, %f, %f); expected %d, got %d", 
					tt.n, tt.l, tt.u, tt.v, tt.idx, result)
			}
		})
	}
}

