package floats

import (
	"math"
	"testing"
)

func TestNorm(t *testing.T) {
	tests := []struct {
		slice   []float64
		L       float64
		expected float64
	}{
		// Test with an empty slice
		{[]float64{}, 1, 0},
		{[]float64{}, 2, 0},
		{[]float64{}, math.Inf(1), 0},

		// Test with L = 1
		{[]float64{1, -1}, 1, 2},
		{[]float64{3, -4, 5}, 1, 12},

		// Test with L = 2
		{[]float64{3, 4}, 2, 5}, // 3-4-5 triangle
		{[]float64{1, 2, 2}, 2, 3}, // sqrt(1^2 + 2^2 + 2^2)

		// Test with L = Inf
		{[]float64{1, -2, 3}, math.Inf(1), 3},
		{[]float64{10, -20, 5}, math.Inf(1), 20},

		// Test with positive and negative values
		{[]float64{-3, -4}, 2, 5}, // 3-4-5 triangle
		{[]float64{5, 12}, 1, 17}, // 5 + 12

		// Test with non-integer L
		{[]float64{1, 2, 3}, 3, math.Pow(14, 1/3.0)}, // (1^3 + 2^3 + 3^3)^(1/3)
	}

	for _, test := range tests {
		result := Norm(test.slice, test.L)
		if math.Abs(result-test.expected) > 1e-10 {
			t.Errorf("Norm(%v, %v) = %v; expected %v", test.slice, test.L, result, test.expected)
		}
	}
}

013e5ee407fb258d6a8c3fda98af7c6a:
package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		name       string
		dstLen    int
		l, u      float64
		expected  []float64
		expectPanic bool
	}{
		{
			name: "Standard case",
			dstLen: 5,
			l: 0, 
			u: 4, 
			expected: []float64{0, 1, 2, 3, 4}, 
			expectPanic: false,
		},
		{
			name: "Dst length of 2",
			dstLen: 2,
			l: 1, 
			u: 3, 
			expected: []float64{1, 3}, 
			expectPanic: false,
		},
		{
			name: "Panic on length less than 2",
			dstLen: 1,
			l: 1, 
			u: 3, 
			expectPanic: true,
		},
		{
			name: "NaN in lower bound",
			dstLen: 5,
			l: math.NaN(), 
			u: 4, 
			expected: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 4}, 
			expectPanic: false,
		},
		{
			name: "NaN in upper bound",
			dstLen: 5,
			l: 0, 
			u: math.NaN(), 
			expected: []float64{0, math.NaN(), math.NaN(), math.NaN(), math.NaN()}, 
			expectPanic: false,
		},
		{
			name: "Infinity in lower bound",
			dstLen: 5,
			l: math.Inf(1), 
			u: 4, 
			expected: []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), 4}, 
			expectPanic: false,
		},
		{
			name: "Infinity in upper bound",
			dstLen: 5,
			l: 0, 
			u: math.Inf(1), 
			expected: []float64{0, 0, 0, 0, math.Inf(1)}, 
			expectPanic: false,
		},
		{
			name: "Both bounds are infinity",
			dstLen: 5,
			l: math.Inf(1), 
			u: math.Inf(1), 
			expected: []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1)}, 
			expectPanic: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			dst := make([]float64, tt.dstLen)
			if tt.expectPanic {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("Expected panic but did not get one")
					}
				}()
			}

			result := Span(dst, tt.l, tt.u)

			if !tt.expectPanic {
				if !equalSlices(result, tt.expected) {
					t.Errorf("Expected %v, got %v", tt.expected, result)
				}
			}
		})
	}
}

// Helper function to compare slices for equality
func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !floatEquals(a[i], b[i]) {
			return false
		}
	}
	return true
}

// Helper function to compare floating point numbers for equality
func floatEquals(a, b float64) bool {
	return math.IsNaN(a) && math.IsNaN(b) || a == b
}

