package floats

import (
	"testing"
)

func TestSub(t *testing.T) {
	tests := []struct {
		dst      []float64
		s        []float64
		expected []float64
	}{
		{
			dst:      []float64{5.0, 6.0, 7.0},
			s:        []float64{1.0, 2.0, 3.0},
			expected: []float64{4.0, 4.0, 4.0},
		},
		{
			dst:      []float64{-1.0, -2.0, -3.0},
			s:        []float64{-1.0, 2.0, 3.0},
			expected: []float64{0.0, -4.0, -6.0},
		},
		{
			dst:      []float64{0.0, 0.0, 0.0},
			s:        []float64{0.0, 0.0, 0.0},
			expected: []float64{0.0, 0.0, 0.0},
		},
	}

	for _, test := range tests {
		dstCopy := make([]float64, len(test.dst))
		copy(dstCopy, test.dst)
		Sub(dstCopy, test.s)

		for i, val := range dstCopy {
			if val != test.expected[i] {
				t.Errorf("Sub(%v, %v) = %v; want %v", test.dst, test.s, dstCopy, test.expected)
			}
		}
	}

	// Test panic for mismatched lengths
	defer func() {
		if r := recover(); r == nil {
			t.Errorf("Sub did not panic with mismatched lengths")
		}
	}()

	Sub([]float64{1.0, 2.0}, []float64{1.0})

}

