// nearestidx_test.go
package floats

import (
	"math"
	"testing"
)

func TestNearestIdx(t *testing.T) {
	tests := []struct {
		s    []float64
		v    float64
		want int
	}{
		{[]float64{1.0, 2.0, 3.0}, 2.0, 1},   // Exact match
		{[]float64{1.0, 2.0, 3.0}, 2.5, 1},   // Between two values
		{[]float64{1.0, 2.0, 3.0}, 1.5, 0},   // Closer to the lower bound
		{[]float64{1.0, 2.0, 3.0}, 3.0, 2},   // Exact match at the upper bound
		{[]float64{1.0, 1.0, 1.0}, 1.0, 0},   // All identical
		{[]float64{1.0, 2.0, 2.0}, 2.1, 1},   // Between duplicates
		{[]float64{-1.0, 0.0, 1.0}, 0.5, 2},  // Positive and negative values
		{[]float64{-1.0, 0.0, 1.0}, -0.5, 0}, // Negative value
	}

	for _, tt := range tests {
		t.Run(fmt.Sprintf("NearestIdx(%v, %v)", tt.s, tt.v), func(t *testing.T) {
			got := NearestIdx(tt.s, tt.v)
			if got != tt.want {
				t.Errorf("got %d, want %d", got, tt.want)
			}
		})
	}

	// Test for panic on zero length slice
	defer func() {
		if r := recover(); r == nil {
			t.Errorf("Expected panic on zero length slice, but didn't get one")
		}
	}()
	NearestIdx([]float64{}, 1.0)

	// Test for NaN value
	if got := NearestIdx([]float64{1.0, 2.0, 3.0}, math.NaN()); got != 0 {
		t.Errorf("got %d, want 0 for NaN input", got)
	}

	// Test for positive infinity
	if got := NearestIdx([]float64{1.0, 2.0, 3.0}, math.Inf(1)); got != 2 {
		t.Errorf("got %d, want 2 for positive infinity", got)
	}

	// Test for negative infinity
	if got := NearestIdx([]float64{1.0, 2.0, 3.0}, math.Inf(-1)); got != 0 {
		t.Errorf("got %d, want 0 for negative infinity", got)
	}
}

