import (
	"math"
	"testing"
)

func TestNearestIdxForSpan(t *testing.T) {
	tests := []struct {
		n, l, u, v float64
		expected   int
		expectPanic bool
	}{
		{3, 0, 10, 5, 1, false},          // Exactly in the middle
		{5, 0, 10, 3, 1, false},          // Closer to the start
		{5, 0, 10, 8, 4, false},          // Closer to the end
		{4, 0, 10, 10, 3, false},         // At the upper bound
		{4, 0, 10, 0, 0, false},          // At the lower bound
		{2, -10, -5, -7, 0, false},       // Negative range
		{5, -10, -5, -12, 0, false},      // Outside lower bound
		{5, -5, 0, 2, 4, false},           // Outside upper bound
		{3, 0, 0, 0, 0, false},           // Edge case where l == u == v
		{1, 0, 10, 5, 0, true},           // n < 2 should panic
		{3, 0, math.NaN(), 5, 2, false}, // Upper bound is NaN
		{3, math.NaN(), 10, 5, 0, false}, // Lower bound is NaN
		{3, math.Inf(-1), math.Inf(1), 0, 1, false}, // Inf bounds
		{3, math.Inf(1), math.Inf(-1), 0, 2, false}, // Reverse Inf bounds
	}

	for _, test := range tests {
		func() {
			defer func() {
				if r := recover(); (r != nil) != test.expectPanic {
					t.Errorf("Expected panic: %v, but got: %v", test.expectPanic, r)
				}
			}()
			result := NearestIdxForSpan(int(test.n), test.l, test.u, test.v)
			if result != test.expected {
				t.Errorf("NearestIdxForSpan(%d, %f, %f, %f) = %d; expected %d", int(test.n), test.l, test.u, test.v, result, test.expected)
			}
