// span_test.go
package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		name    string
		dst     []float64
		l, u    float64
		want    []float64
		wantErr bool
	}{
		{
			name: "basic span",
			dst:  make([]float64, 5),
			l:    0,
			u:    10,
			want: []float64{0, 2.5, 5, 7.5, 10},
		},
		{
			name: "negative span",
			dst:  make([]float64, 5),
			l:    -10,
			u:    -1,
			want: []float64{-10, -7.25, -4.5, -1.75, -1},
		},
		{
			name: "same l and u",
			dst:  make([]float64, 5),
			l:    5,
			u:    5,
			want: []float64{5, 5, 5, 5, 5},
		},
		{
			name: "span with NaN lower",
			dst:  make([]float64, 5),
			l:    math.NaN(),
			u:    10,
			want: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 10},
		},
		{
			name: "span with NaN upper",
			dst:  make([]float64, 5),
			l:    0,
			u:    math.NaN(),
			want: []float64{0, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
		},
		{
			name: "panics on dst length < 2",
			dst:  make([]float64, 1),
			l:    0,
			u:    5,
			wantErr: true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if tt.wantErr {
				defer func() {
					if recover() == nil {
						t.Errorf("expected panic, got no panic")
					}
				}()
			}

			got := Span(tt.dst, tt.l, tt.u)
			if !compareSlices(got, tt.want) {
				t.Errorf("got %v, want %v", got, tt.want)
			}
		})
	}
}

func compareSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !isEqual(a[i], b[i]) {
			return false
		}
	}
	return true
}

func isEqual(a, b float64) bool {
	return (math.IsNaN(a) && math.IsNaN(b)) || (a == b)
}

