package floats

import (
	"math"
	"testing"
)

func TestNorm(t *testing.T) {
	tests := []struct {
		slice []float64
		L     float64
		want  float64
	}{
		{[]float64{1, 2, 3}, 1, 6},                          // L1 norm
		{[]float64{1, 2, 3}, 2, math.Sqrt(14)},             // L2 norm
		{[]float64{1, -2, 3}, 2, math.Sqrt(14)},            // L2 norm (mixed signs)
		{[]float64{1, 2, 3}, math.Inf(1), 3},                // L-inf norm
		{[]float64{-1, -2, -3}, math.Inf(1), 3},             // L-inf norm (negative)
		{[]float64{}, 1, 0},                                 // empty slice
		{[]float64{0}, 1, 0},                                // single element zero
		{[]float64{1, 2, 3}, 3, math.Pow(14, 1.0/3.0)},    // L3 norm
		{[]float64{5, 12, 13}, 2, 13},                       // Pythagorean triple with L2
	}

	for _, test := range tests {
		got := Norm(test.slice, test.L)
		if got != test.want {
			t.Errorf("Norm(%v, %v) = %v; want %v", test.slice, test.L, got, test.want)
		}
	}
}

