package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		dst       []float64
		l, u     float64
		expected []float64
	}{
		{
			dst:       make([]float64, 5),
			l:         0,
			u:         4,
			expected:  []float64{0, 1, 2, 3, 4},
		},
		{
			dst:       make([]float64, 3),
			l:         1,
			u:         1,
			expected:  []float64{1, 1, 1},
		},
		{
			dst:       make([]float64, 4),
			l:         -2,
			u:         2,
			expected:  []float64{-2, -1, 0, 2},
		},
		{
			dst:       make([]float64, 5),
			l:         math.NaN(),
			u:         3,
			expected:  []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 3},
		},
		{
			dst:       make([]float64, 5),
			l:         2,
			u:         math.NaN(),
			expected:  []float64{2, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
		},
		{
			dst:       make([]float64, 6),
			l:         math.Inf(-1),
			u:         math.Inf(1),
			expected:  []float64{math.Inf(-1), math.Inf(-1), math.Inf(-1), 0, math.Inf(1), math.Inf(1)},
		},
	}

	for _, tt := range tests {
		result := Span(tt.dst, tt.l, tt.u)
		for i := range tt.expected {
			if math.IsNaN(tt.expected[i]) {
				if !math.IsNaN(result[i]) {
					t.Errorf("Expected NaN at index %d, got %v", i, result[i])
				}
			} else if result[i] != tt.expected[i] {
				t.Errorf("Expected %v at index %d, got %v", tt.expected[i], i, result[i])
			}
		}
	}

	// Test panic for lengths less than 2
	defer func() {
		if r := recover(); r == nil {
			t.Errorf("Expected panic for length < 2 but did not panic")
		}
	}()
	Span(make([]float64, 1), 0, 1)
}

