func TestNearestIdxForSpan(t *testing.T) {
	tests := []struct {
		n       int
		l, u, v float64
		want    int
	}{
		{5, 0, 10, 5, 2},                  // v is in the middle of the span
		{5, 0, 10, 0, 0},                  // v is equal to the lower bound
		{5, 0, 10, 10, 4},                 // v is equal to the upper bound
		{5, 0, 10, 10.1, 4},               // v is just above upper bound
		{5, 0, 10, -0.1, 0},               // v is just below lower bound
		{5, 10, 0, 5, 2},                  // l > u, v in the middle
		{5, 10, 0, 10, 0},                 // l > u, v equal to upper bound
		{5, 10, 0, 0, 4},                  // l > u, v equal to lower bound
		{5, 10, 0, 15, 0},                 // l > u, v just above upper bound
		{5, 10, 0, -5, 4},                 // l > u, v just below lower bound
		{3, 0, 1, 0.5, 1},                 // edge case with n = 3
		{2, 0, 1, 0.5, 0},                 // edge case with n = 2
		{2, 0, 1, 1, 1},                   // edge case with n = 2, v equal to upper bound
		{2, 0, 1, -1, 0},                  // edge case with n = 2, v below range
		{2, 0, 1, 2, 1},                   // edge case with n = 2, v above range
	}

	for _, tt := range tests {
		t.Run("", func(t *testing.T) {
			got := NearestIdxForSpan(tt.n, tt.l, tt.u, tt.v)
			if got != tt.want {
				t.Errorf("NearestIdxForSpan(%d, %f, %f, %f) = %d; want %d", tt.n, tt.l, tt.u, tt.v, got, tt.want)
			}
		})
	}

	// Test panic for n < 2
	defer func() {
		if r := recover(); r == nil {
			t.Errorf("NearestIdxForSpan(1, 0, 1, 0) did not panic")
		}
	}()
	NearestIdxForSpan(1, 0, 1, 0)

	// Test NaN case
	if got := NearestIdxForSpan(5, 0, 10, math.NaN()); got != 0 {
		t.Errorf("NearestIdxForSpan(5, 0, 10, NaN) = %d; want %d", got, 0)
	}
}

