func TestSpan(t *testing.T) {
	tests := []struct {
		dst     []float64
		l, u    float64
		want    []float64
		wantErr bool
	}{
		{
			dst:     make([]float64, 5),
			l:       0,
			u:       10,
			want:    []float64{0, 2.5, 5, 7.5, 10},
			wantErr: false,
		},
		{
			dst:     make([]float64, 3),
			l:       1,
			u:       3,
			want:    []float64{1, 2, 3},
			wantErr: false,
		},
		{
			dst:     make([]float64, 0),
			l:       1,
			u:       3,
			wantErr: true,
		},
		{
			dst:     make([]float64, 1),
			l:       1,
			u:       3,
			wantErr: true,
		},
		{
			dst:     make([]float64, 5),
			l:       math.NaN(),
			u:       10,
			want:    []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 10},
			wantErr: false,
		},
		{
			dst:     make([]float64, 5),
			l:       0,
			u:       math.NaN(),
			want:    []float64{0, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
			wantErr: false,
		},
		{
			dst:     make([]float64, 5),
			l:       math.Inf(1),
			u:       math.Inf(-1),
			want:    []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(-1)},
			wantErr: false,
		},
		{
			dst:     make([]float64, 5),
			l:       math.Inf(1),
			u:       10,
			want:    []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), 10},
			wantErr: false,
		},
		{
			dst:     make([]float64, 5),
			l:       0,
			u:       math.Inf(-1),
			want:    []float64{0, 0, 0, 0, math.Inf(-1)},
			wantErr: false,
		},
	}

	for _, tt := range tests {
		if tt.wantErr {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Span() did not panic for dst length %d l = %v, u = %v", len(tt.dst), tt.l, tt.u)
				}
			}()
		}
		got := Span(tt.dst, tt.l, tt.u)
		if !compareSlices(got, tt.want) {
			t.Errorf("Span(%v, %v, %v) = %v; want %v", tt.dst, tt.l, tt.u, got, tt.want)
		}
	}
}

func compareSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !floatEqual(a[i], b[i]) {
			return false
		}
	}
	return true
}

func floatEqual(a, b float64) bool {
	return a == b || (math.IsNaN(a) && math.IsNaN(b))
}

