		},
		{
			dst:   make([]float64, 3),
			l:     0.0,
			u:     2.0,
			want:  []float64{0.0, 1.0, 2.0},
			panics: false,
		},
		{
			dst:   make([]float64, 2),
			l:     -1.0,
			u:     1.0,
			want:  []float64{-1.0, 1.0},
			panics: false,
		},
		{
			dst:   make([]float64, 0),
			l:     0.0,
			u:     1.0,
			panics: true,
		},
		{
			dst:   make([]float64, 1),
			l:     0.0,
			u:     1.0,
			panics: true,
		},
		{
			dst:   make([]float64, 5),
			l:     math.NaN(),
			u:     1.0,
			want:  []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 1.0},
			panics: false,
		},
		{
			dst:   make([]float64, 5),
			l:     0.0,
			u:     math.NaN(),
			want:  []float64{0.0, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
			panics: false,
		},
		{
			dst:   make([]float64, 5),
			l:     math.Inf(-1),
			u:     math.Inf(1),
			want:  []float64{math.Inf(-1), -1, 0, 1, math.Inf(1)},
			panics: false,
		},
		{
			dst:   make([]float64, 5),
			l:     math.Inf(-1),
			u:     0.0,
			want:  []float64{math.Inf(-1), -0.75, -0.5, -0.25, 0.0},
			panics: false,
		},
	}

	for _, test := range tests {
		if test.panics {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Expected panic for input %v but did not panic", test)
				}
			}()
		}

		got := Span(test.dst, test.l, test.u)

		if !test.panics && !equalSlices(got, test.want) {
			t.Errorf("Span(%v, %f, %f) = %v; want %v", test.dst, test.l, test.u, got, test.want)
		}
	}
}

func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !(math.IsNaN(a[i]) && math.IsNaN(b[i]) || a[i] == b[i]) {
			return false
		}
	}
	return true
}

