		t.Run(tt.name, func(t *testing.T) {
			if tt.expectPanic {
				defer func() {
					if recover() == nil {
						t.Errorf("expected panic but did not panic")
					}
				}()
			}
			result := Span(tt.dst, tt.l, tt.u)
			if !tt.expectPanic && !equalSlices(result, tt.expected) {
				t.Errorf("expected %v, got %v", tt.expected, result)
			}
		})
	}
}

// Helper function to compare slices for equality
func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !equalFloat(a[i], b[i]) {
			return false
		}
	}
	return true
}

// Helper function to compare float64 values for equality considering NaN
func equalFloat(a, b float64) bool {
	if math.IsNaN(a) && math.IsNaN(b) {
		return true
	}
	return a == b
}

