package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		name       string
		dstLength  int
		l, u       float64
		expected   []float64
		expectPanic bool
	}{
		{
			name:       "normal case",
			dstLength:  5,
			l:          0,
			u:          4,
			expected:   []float64{0, 1, 2, 3, 4},
			expectPanic: false,
		},
		{
			name:       "same lower and upper bounds",
			dstLength:  5,
			l:          2,
			u:          2,
			expected:   []float64{2, 2, 2, 2, 2},
			expectPanic: false,
		},
		{
			name:       "length less than two",
			dstLength:  1,
			l:          1,
			u:          2,
			expectPanic: true,
		},
		{
			name:       "NaN lower bound",
			dstLength:  5,
			l:          math.NaN(),
			u:          2,
			expected:   []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 2},
			expectPanic: false,
		},
		{
			name:       "NaN upper bound",
			dstLength:  5,
			l:          1,
			u:          math.NaN(),
			expected:   []float64{1, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
			expectPanic: false,
		},
		{
			name:       "infinity bounds",
			dstLength:  5,
			l:          math.Inf(1),
			u:          math.Inf(1),
			expected:   []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1)},
			expectPanic: false,
		},
		{
			name:       "from -Inf to Inf",
			dstLength:  5,
			l:          math.Inf(-1),
			u:          math.Inf(1),
			expected:   []float64{math.Inf(-1), 0, 0, 0, math.Inf(1)},
			expectPanic: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			dst := make([]float64, tt.dstLength)

			if tt.expectPanic {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("Expected panic but didn't get one")
					}
				}()
			}

			result := Span(dst, tt.l, tt.u)

			if !tt.expectPanic {
				if len(result) != len(tt.expected) {
					t.Errorf("Expected length %d, got %d", len(tt.expected), len(result))
				}
				for i, v := range result {
					if !equalFloat(v, tt.expected[i]) {
						t.Errorf("At index %d: expected %v, got %v", i, tt.expected[i], v)
					}
				}
			}
		})
	}
}

func equalFloat(a, b float64) bool {
	return (math.IsNaN(a) && math.IsNaN(b)) || (a == b)
}

