		// Test with L = 0, which should be handled by the Count function
		// (Not applicable here, as L should be a positive number; just for completeness)
		{[]float64{}, 0, 0}, // edge case (norm not defined for L == 0)

		// Test with L = math.Inf(1) with mixed values
		{[]float64{-5, -10, 3.2}, math.Inf(1), 10}, // max absolute value with negatives
	}

	for _, test := range tests {
		result := Norm(test.slice, test.L)
		if !floatEquals(result, test.expected) {
			t.Errorf("Norm(%v, %v) = %v; expected %v", test.slice, test.L, result, test.expected)
		}
	}
}

// Helper function to compare floating point numbers for equality
func floatEquals(a, b float64) bool {
	const epsilon = 1e-9
	return math.Abs(a-b) < epsilon
}

