package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		dst     []float64
		l, u    float64
		want    []float64
		wantPanic bool
	}{
		{
			dst:     make([]float64, 5),
			l:       0,
			u:       4,
			want:    []float64{0, 1, 2, 3, 4},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 3),
			l:       -1,
			u:       1,
			want:    []float64{-1, 0, 1},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 2),
			l:       1,
			u:       1,
			want:    []float64{1, 1},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 0),
			l:       0,
			u:       1,
			want:    nil,
			wantPanic: true,
		},
		{
			dst:     make([]float64, 1),
			l:       0,
			u:       1,
			want:    nil,
			wantPanic: true,
		},
		{
			dst:     make([]float64, 5),
			l:       math.NaN(),
			u:       5,
			want:    []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 5},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 5),
			l:       2,
			u:       math.NaN(),
			want:    []float64{2, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 3),
			l:       math.Inf(1),
			u:       1,
			want:    []float64{math.Inf(1), math.Inf(1), 1},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 3),
			l:       1,
			u:       math.Inf(-1),
			want:    []float64{1, math.Inf(-1), math.Inf(-1)},
			wantPanic: false,
		},
	}

	for _, tt := range tests {
		if tt.wantPanic {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Span(%v, %v, %v) did not panic", tt.dst, tt.l, tt.u)
				}
			}()
		}
		got := Span(tt.dst, tt.l, tt.u)
		if !equalSlices(got, tt.want) {
			t.Errorf("Span(%v, %v, %v) = %v; want %v", tt.dst, tt.l, tt.u, got, tt.want)
		}
	}
}

func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if math.IsNaN(a[i]) && math.IsNaN(b[i]) {
			continue
		}
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

