func TestSpan(t *testing.T) {
	tests := []struct {
		dst       []float64
		l, u     float64
		expect    []float64
		expectPanic bool // indicates whether this test should panic
	}{
		{
			dst:       make([]float64, 5),
			l:         1,
			u:         5,
			expect:    []float64{1, 2, 3, 4, 5},
			expectPanic: false,
		},
		{
			dst:       make([]float64, 2),
			l:         0,
			u:         10,
			expect:    []float64{0, 10},
			expectPanic: false,
		},
		{
			dst:       make([]float64, 3),
			l:         -2,
			u:         2,
			expect:    []float64{-2, 0, 2},
			expectPanic: false,
		},
		{
			dst:       make([]float64, 0),
			l:         1,
			u:         2,
			expect:    nil, // expects panic
			expectPanic: true,
		},
		{
			dst:       make([]float64, 1),
			l:         1,
			u:         2,
			expect:    nil, // expects panic
			expectPanic: true,
		},
		{
			dst:       make([]float64, 5),
			l:         math.NaN(),
			u:         5,
			expect:    []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 5},
			expectPanic: false,
		},
		{
			dst:       make([]float64, 5),
			l:         1,
			u:         math.NaN(),
			expect:    []float64{1, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
			expectPanic: false,
		},
		{
			dst:       make([]float64, 5),
			l:         math.Inf(1),
			u:         1,
			expect:    []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), 1},
			expectPanic: false,
		},
		{
			dst:       make([]float64, 5),
			l:         1,
			u:         math.Inf(-1),
			expect:    []float64{1, math.Inf(-1), math.Inf(-1), math.Inf(-1), math.Inf(-1)},
			expectPanic: false,
		},
	}

	for _, tt := range tests {
		if tt.expectPanic {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("expected panic for dst: %v, l: %v, u: %v", tt.dst, tt.l, tt.u)
				}
			}()
		}

		result := Span(tt.dst, tt.l, tt.u)
		if !tt.expectPanic && !equalSlices(result, tt.expect) {
			t.Errorf("Span(%v, %v, %v) = %v; want %v", tt.dst, tt.l, tt.u, result, tt.expect)
		}
	}
}

func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

