		// Test for L = Infinity (max absolute value)
		{[]float64{1, -2, 3}, math.Inf(1), 3}, // max(|1|, |-2|, |3|) = 3
		{[]float64{-1, -5, 2}, math.Inf(1), 5}, // max(|-1|, |-5|, |2|) = 5

		// Test for generic L
		{[]float64{1, 2, 3}, 3, math.Pow(14, 1.0/3.0)}, // (1Â³ + 2Â³ + 3Â³)^(1/3)
		{[]float64{4, -5, 6}, 4, math.Pow(77, 1.0/4.0)}, // (4â´ + |-5|â´ + 6â´)^(1/4)

