func TestSpan(t *testing.T) {
	tests := []struct {
		dst    []float64
		l, u   float64
		want   []float64
		wantPanic bool
	}{
		{
			dst:    make([]float64, 5),
			l:      0,
			u:      4,
			want:   []float64{0, 1, 2, 3, 4},
			wantPanic: false,
		},
		{
			dst:    make([]float64, 3),
			l:      2,
			u:      6,
			want:   []float64{2, 4, 6},
			wantPanic: false,
		},
		{
			dst:    make([]float64, 2),
			l:      -10,
			u:      10,
			want:   []float64{-10, 10},
			wantPanic: false,
		},
		{
			dst:    make([]float64, 0),
			l:      1,
			u:      5,
			want:   nil,
			wantPanic: true,
		},
		{
			dst:    make([]float64, 1),
			l:      1,
			u:      5,
			want:   nil,
			wantPanic: true,
		},
		{
			dst:    make([]float64, 4),
			l:      math.NaN(),
			u:      2,
			want:   []float64{math.NaN(), math.NaN(), math.NaN(), 2},
			wantPanic: false,
		},
		{
			dst:    make([]float64, 4),
			l:      -math.Inf(1),
			u:      math.Inf(0),
			want:   []float64{-math.Inf(1), -math.Inf(1), 0, math.Inf(0)},
			wantPanic: false,
		},
		{
			dst:    make([]float64, 4),
			l:      0,
			u:      math.Inf(0),
			want:   []float64{0, 0, 0, math.Inf(0)},
			wantPanic: false,
		},
	}

	for _, tt := range tests {
		if tt.wantPanic {
			func() {
				defer func() { recover() }()
				Span(tt.dst, tt.l, tt.u)
				t.Errorf("Span(%v, %v, %v) did not panic", tt.dst, tt.l, tt.u)
			}()
		} else {
			got := Span(tt.dst, tt.l, tt.u)
			for i, v := range tt.want {
				if math.IsNaN(v) {
					if !math.IsNaN(got[i]) {
						t.Errorf("Span() = %v, want NaN at index %d", got, i)
					}
				} else if got[i] != v {
					t.Errorf("Span() = %v, want %v at index %d", got, tt.want, i)
				}
			}
		}
	}
}

