package floats

import (
	"math"
	"testing"
)

func TestDistance(t *testing.T) {
	tests := []struct {
		s        []float64
		t        []float64
		L        float64
		expected float64
	}{
		{[]float64{1, 2, 3}, []float64{1, 2, 3}, 1, 0},                      // Distance should be 0
		{[]float64{1, 2, 3}, []float64{4, 5, 6}, 1, 9},                     // Manhattan distance
		{[]float64{1, 2, 3}, []float64{4, 5, 6}, 2, math.Sqrt(27)},         // Euclidean distance
		{[]float64{1, 2, 3}, []float64{4, 5, 6}, math.Inf(1), 3},           // Max distance
		{[]float64{}, []float64{}, 1, 0},                                   // Distance of empty slices
		{[]float64{1}, []float64{1}, 2, 0},                                 // Single element similarity
	}

	for _, test := range tests {
		t.Run("", func(t *testing.T) {
			defer func() {
				if r := recover(); r != nil && len(test.s) != len(test.t) {
					// panic expected due to length mismatch
					return
				} else if r != nil {
					t.Errorf("Distance panicked: %v", r)
				}
			}()
			result := Distance(test.s, test.t, test.L)
			if !almostEqual(result, test.expected) {
				t.Errorf("Distance(%v, %v, %v) = %v; want %v", test.s, test.t, test.L, result, test.expected)
			}
		})
	}
}

// Helper function to compare two float64 values with a tolerance for floating point arithmetic
func almostEqual(a, b float64) bool {
	const tolerance = 1e-9
	return math.Abs(a-b) < tolerance
}

