package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		dst     []float64
		l, u    float64
		want    []float64
		wantPanic bool
	}{
		{
			dst:     make([]float64, 5),
			l:       0,
			u:       4,
			want:    []float64{0, 1, 2, 3, 4},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 3),
			l:       -1,
			u:       1,
			want:    []float64{-1, 0, 1},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 2),
			l:       1,
			u:       1,
			want:    []float64{1, 1},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 1),
			l:       1,
			u:       2,
			wantPanic: true,
		},
		{
			dst:     make([]float64, 4),
			l:       math.NaN(),
			u:       2,
			want:    []float64{math.NaN(), math.NaN(), math.NaN(), 2},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 4),
			l:       2,
			u:       math.NaN(),
			want:    []float64{2, math.NaN(), math.NaN(), math.NaN()},
			wantPanic: false,
		},
		{
			dst:     make([]float64, 4),
			l:       math.Inf(-1),
			u:       math.Inf(1),
			want:    []float64{math.Inf(-1), math.NaN(), math.NaN(), math.Inf(1)},
			wantPanic: false,
		},
	}

	for _, tt := range tests {
		if tt.wantPanic {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("expected panic for dst: %v, l: %v, u: %v", tt.dst, tt.l, tt.u)
				}
			}()
		}
		got := Span(tt.dst, tt.l, tt.u)
		if !equalSlices(got, tt.want) {
			t.Errorf("Span(%v, %v, %v) = %v; want %v", tt.dst, tt.l, tt.u, got, tt.want)
		}
	}
}

// equalSlices checks if two slices of float64 are equal.
func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !floatEqual(a[i], b[i]) {
			return false
		}
	}
	return true
}

// floatEqual checks if two float64 values are approximately equal.
func floatEqual(a, b float64) bool {
	return (math.IsNaN(a) && math.IsNaN(b)) || (a == b)
}

