package floats

import (
	"testing"
)

func TestDivTo(t *testing.T) {
	tests := []struct {
		name     string
		dst      []float64
		s        []float64
		t        []float64
		expected []float64
		expectPanic bool
	}{
		{
			name:     "Valid division",
			dst:      []float64{0, 0, 0},
			s:        []float64{4, 9, 16},
			t:        []float64{2, 3, 4},
			expected: []float64{2, 3, 4},
			expectPanic: false,
		},
		{
			name:     "Panic on length mismatch - dst length",
			dst:      []float64{0, 0}, // Length 2
			s:        []float64{1, 2, 3}, // Length 3
			t:        []float64{1, 2, 3}, // Length 3
			expected: nil,
			expectPanic: true,
		},
		{
			name:     "Panic on length mismatch - s length",
			dst:      []float64{0, 0, 0}, // Length 3
			s:        []float64{4, 5}, // Length 2
			t:        []float64{2, 3}, // Length 2
			expected: nil,
			expectPanic: true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if tt.expectPanic {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("Expected panic for test %s, but did not get one", tt.name)
					}
				}()
			}

			result := DivTo(tt.dst, tt.s, tt.t)

			if !tt.expectPanic && !equalSlices(result, tt.expected) {
				t.Errorf("Got %v, expected %v", result, tt.expected)
			}
		})
	}
}

// Helper function to compare two slices for equality
func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

