		{3, 0, math.NaN(), 5, 0, false},   // NaN upper bound
		{3, math.Inf(-1), math.Inf(1), 0, 0, false}, // Inf bounds
		{3, -1, 1, 2, 2, false},            // Bounds around zero
		{2, 0, 10, math.NaN(), 0, false},  // NaN value
		{1, 0, 10, 5, 0, true},             // Panic case: n < 2
	}

	for _, tt := range tests {
		if tt.expectPanic {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Expected panic for n=%d, l=%f, u=%f, v=%f, but did not", tt.n, tt.l, tt.u, tt.v)
				}
			}()
		}
		result := NearestIdxForSpan(tt.n, tt.l, tt.u, tt.v)
		if result != tt.expected {
			t.Errorf("Expected index %d for n=%d, l=%f, u=%f, v=%f, got %d", tt.expected, tt.n, tt.l, tt.u, tt.v, result)
		}
	}
}

