		inds      []int
		f         func(float64) bool
		s         []float64
		k         int
		expected  []int
		expectErr bool
	}{
		{
			name:      "finding first k elements true",
			inds:      []int{},
			f:         func(x float64) bool { return x > 0 },
			s:         []float64{-1, 2, 3, -4, 5},
			k:         2,
			expected:  []int{1, 2},
			expectErr: false,
		},
		{
			name:      "k is 0",
			inds:      []int{},
			f:         func(x float64) bool { return x > 0 },
			s:         []float64{-1, 2, 3, -4, 5},
			k:         0,
			expected:  []int{},
			expectErr: false,
		},
		{
			name:      "k is negative, return all found elements",
			inds:      []int{},
			f:         func(x float64) bool { return x > 0 },
			s:         []float64{-1, 2, 3, -4, 5},
			k:         -1,
			expected:  []int{1, 2, 4},
			expectErr: false,
		},
		{
			name:      "not enough elements found",
			inds:      []int{},
			f:         func(x float64) bool { return x < 0 },
			s:         []float64{1, 2, 3},
			k:         2,
			expected:  []int{1},
			expectErr: true,
		},
		{
			name:      "no elements found",
			inds:      []int{},
			f:         func(x float64) bool { return x < 0 },
			s:         []float64{1, 2, 3},
			k:         1,
			expected:  []int{},
			expectErr: true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			inds, err := Find(tt.inds, tt.f, tt.s, tt.k)
			if (err != nil) != tt.expectErr {
				t.Fatalf("expected error: %v, got: %v", tt.expectErr, err)
			}
			if !equalSlices(inds, tt.expected) {
				t.Fatalf("expected: %v, got: %v", tt.expected, inds)
			}
		})
	}
}

func equalSlices(a, b []int) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

