package floats

import (
	"math"
	"testing"
)

func TestNorm(t *testing.T) {
	tests := []struct {
		name     string
		slice    []float64
		L        float64
		expected float64
	}{
		{
			name:     "L1 norm of simple positive values",
			slice:    []float64{1, 2, 3},
			L:        1,
			expected: 6,
		},
		{
			name:     "L2 norm of simple positive values",
			slice:    []float64{3, 4},
			L:        2,
			expected: 5, // 3^2 + 4^2 = 25, sqrt(25) = 5
		},
		{
			name:     "Lâˆž norm (max absolute value)",
			slice:    []float64{-1, -2, 2, 3},
			L:        math.Inf(1),
			expected: 3,
		},
		{
			name:     "L1 norm of negative values",
			slice:    []float64{-1, -2, -3},
			L:        1,
			expected: 6,
		},
		{
			name:     "Empty slice",
			slice:    []float64{},
			L:        1,
			expected: 0,
		},
		{
			name:     "L2 norm with zero values",
			slice:    []float64{0, 0, 0},
			L:        2,
			expected: 0,
		},
		{
			name:     "L4 norm of simple positive values",
			slice:    []float64{1, 2, 3, 4},
			L:        4,
			expected: math.Pow(1*1 + 2*2 + 3*3 + 4*4, 1.0/4.0), // (30 ^ (1/4))
		},
		{
			name:     "L-norm for a mix of positive and negative",
			slice:    []float64{-1, 2, -3},
			L:        3,
			expected: math.Pow(math.Abs(-1)+math.Abs(2)+math.Abs(-3), 1.0/3.0), // (6^(1/3))
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result := Norm(tt.slice, tt.L)
			if !almostEqual(result, tt.expected, 1e-9) {
				t.Errorf("expected %v, got %v", tt.expected, result)
			}
		})
	}
}

// almostEqual checks whether two float64 values are approximately equal.
func almostEqual(a, b, epsilon float64) bool {
	return math.Abs(a-b) < epsilon
}

