func TestSpan(t *testing.T) {
	tests := []struct {
		name     string
		dst      []float64
		l, u     float64
		expected []float64
	}{
		{
			name:     "regular case",
			dst:      make([]float64, 5),
			l:        0,
			u:        10,
			expected: []float64{0, 2.5, 5, 7.5, 10},
		},
		{
			name:     "inf l and u",
			dst:      make([]float64, 5),
			l:        math.Inf(1),
			u:        math.Inf(1),
			expected: []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1)},
		},
		{
			name:     "inf l",
			dst:      make([]float64, 5),
			l:        math.Inf(1),
			u:        10,
			expected: []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), 10},
		},
		{
			name:     "inf u",
			dst:      make([]float64, 5),
			l:        0,
			u:        math.Inf(1),
			expected: []float64{0, 2.5, 5, 7.5, math.Inf(1)},
		},
		{
			name:     "NaN l",
			dst:      make([]float64, 5),
			l:        math.NaN(),
			u:        10,
			expected: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 10},
		},
		{
			name:     "NaN u",
			dst:      make([]float64, 5),
			l:        0,
			u:        math.NaN(),
			expected: []float64{0, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
		},
		{
			name:     "zero length array",
			dst:      make([]float64, 1),
			l:        1,
			u:        10,
			expected: nil, // Expecting a panic
		},
		{
			name:     "one element array",
			dst:      make([]float64, 1),
			l:        1,
			u:        10,
			expected: nil, // Expecting a panic
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if tt.expected == nil {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("Span() did not panic for input: %+v", tt)
					}
				}()
			}
			got := Span(tt.dst, tt.l, tt.u)
			if tt.expected != nil && !equal(got, tt.expected) {
				t.Errorf("Span() = %v, want %v", got, tt.expected)
			}
		})
	}
}

// Helper function to compare slices for equality
func equal(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if (math.IsNaN(a[i]) && !math.IsNaN(b[i])) || (a[i] != b[i]) {
			return false
		}
	}
	return true
}

