		{5, 0, 10, 0, 0},         // Lower bound
		{5, 0, 10, 10, 4},        // Upper bound
		{5, -10, -5, -7.5, 1},    // Negative range, middle point
		{5, -10, -5, -10, 0},     // Negative range, lower bound
		{5, -10, -5, -5, 4},      // Negative range, upper bound
		{3, 0, 1, 0.5, 1},        // Edge case with n=3
		{10, 1, 5, 3, 4},         // Generic case
		{10, 1, 5, 0, 0},         // Out of range, low
		{10, 1, 5, 6, 9},         // Out of range, high
	}

	for _, test := range tests {
		result := NearestIdxForSpan(test.n, test.l, test.u, test.v)
		if result != test.expected {
			t.Errorf("NearestIdxForSpan(%d, %f, %f, %f) = %d; want %d",
				test.n, test.l, test.u, test.v, result, test.expected)
		}
	}

