	defer func() {
		if r := recover(); r == nil {
			t.Errorf("Expected panic for n < 2")
		}
	}()
	NearestIdxForSpan(1, 1, 5, 3)

	// Test NaN case
	result := NearestIdxForSpan(5, 0, 10, math.NaN())
	if result != 0 {
		t.Errorf("Expected 0 for NaN value; got %d", result)
	}

	// Test cases for +Inf and -Inf
	if result := NearestIdxForSpan(5, math.Inf(-1), math.Inf(1), 0); result != 0 {
		t.Errorf("Expected 0 for values between -Inf and Inf; got %d", result)
	}
	if result := NearestIdxForSpan(2, math.Inf(-1), math.Inf(1), math.Inf(1)); result != 1 {
		t.Errorf("Expected 1 for v = +Inf; got %d", result)
