func TestFind(t *testing.T) {
	tests := []struct {
		inds     []int
		f        func(float64) bool
		s        []float64
		k        int
		expected []int
		err      error
	}{
		{
			inds:     nil,
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{-1, 2, 3, -4, 5},
			k:        3,
			expected: []int{1, 2, 4},
			err:      nil,
		},
		{
			inds:     nil,
			f:        func(x float64) bool { return x < 0 },
			s:        []float64{-1, 2, 3, -4, 5},
			k:        2,
			expected: []int{0, 3},
			err:      nil,
		},
		{
			inds:     nil,
			f:        func(x float64) bool { return x > 10 },
			s:        []float64{1, 2, 3},
			k:        1,
			expected: nil,
			err:      errors.New("floats: insufficient elements found"),
		},
		{
			inds:     nil,
			f:        func(x float64) bool { return x < 0 },
			s:        []float64{},
			k:        -1,
			expected: nil,
			err:      nil,
		},
		{
			inds:     nil,
			f:        func(x float64) bool { return x == 0 },
			s:        []float64{0, 0, 0},
			k:        -1,
			expected: []int{0, 1, 2},
			err:      nil,
		},
	}

	for _, tt := range tests {
		inds, err := Find(tt.inds, tt.f, tt.s, tt.k)
		if !equalSlices(inds, tt.expected) || !errors.Is(err, tt.err) {
			t.Errorf("Find(%v, ...): got %v, %v; want %v, %v", tt.inds, inds, err, tt.expected, tt.err)
		}
	}
}

func equalSlices(a, b []int) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

