			u:          3.0,
			want:      []float64{math.NaN(), 3.0},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 2),
			l:          3.0,
			u:          math.NaN(),
			want:      []float64{3.0, math.NaN()},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 3),
			l:          math.Inf(1),
			u:          3.0,
			want:      []float64{math.Inf(1), 3.0, 3.0},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 3),
			l:          1.0,
			u:          math.Inf(-1),
			want:      []float64{1.0, 1.0, math.Inf(-1)},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 1),
			l:          0.0,
			u:          1.0,
			expectPanic: true,
		},
		{
			dst:        nil,
			l:          0.0,
			u:          1.0,
			expectPanic: true,
		},
	}

	for _, tt := range tests {
		if tt.expectPanic {
			defer func() {
