// distance_test.go
package floats

import (
	"math"
	"testing"
)

func TestDistance(t *testing.T) {
	tests := []struct {
		name     string
		s        []float64
		t        []float64
		L        float64
		expected float64
	}{
		{
			name:     "L1 norm with positive values",
			s:        []float64{1, 2, 3},
			t:        []float64{4, 5, 6},
			L:        1,
			expected: 9, // |4-1| + |5-2| + |6-3| = 3 + 3 + 3 = 9
		},
		{
			name:     "L2 norm with positive values",
			s:        []float64{1, 2, 3},
			t:        []float64{4, 5, 6},
			L:        2,
			expected: math.Sqrt(27), // L2 = sqrt((4-1)^2 + (5-2)^2 + (6-3)^2) = sqrt(27)
		},
		{
			name:     "L-infinity norm with mixed values",
			s:        []float64{-1, 2, -3},
			t:        []float64{1, -2, 3},
			L:        math.Inf(1),
			expected: 4, // max(|1 - -1|, |-2 - 2|, |3 - -3|) = 4
		},
		{
			name:     "Zero vector",
			s:        []float64{},
			t:        []float64{},
			L:        2,
			expected: 0,
		},
		{
			name:     "Panic on mismatched lengths",
			s:        []float64{1, 2},
			t:        []float64{1},
			L:        1,
			expected: 0, // This test checks for a panic
		},
	}

	for _, test := range tests {
		if test.name == "Panic on mismatched lengths" {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Expected panic for test %q, but did not get it.", test.name)
				}
			}()
		}

		result := Distance(test.s, test.t, test.L)
		if test.name != "Panic on mismatched lengths" && !floatEquals(result, test.expected) {
			t.Errorf("Test %q failed: expected %v, got %v", test.name, test.expected, result)
		}
	}
}

// floatEquals checks if two float64 values are approximately equal.
func floatEquals(a, b float64) bool {
	const epsilon = 1e-9
	return math.Abs(a-b) < epsilon
}

