package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		dst    []float64
		l, u   float64
		want   []float64
		panics bool
	}{
		// Test case 1: Normal range
		{
			dst:  make([]float64, 5),
			l:    0,
			u:    4,
			want: []float64{0, 1, 2, 3, 4},
		},
		// Test case 2: Reverse range
		{
			dst:  make([]float64, 5),
			l:    4,
			u:    0,
			want: []float64{4, 3, 2, 1, 0},
		},
		// Test case 3: Panics when length of dst < 2
		{
			dst:    make([]float64, 1),
			l:      0,
			u:      1,
			panics: true,
		},
		// Test case 4: Inf to Inf
		{
			dst:  make([]float64, 3),
			l:    math.Inf(-1),
			u:    math.Inf(1),
			want: []float64{math.Inf(-1), 0, math.Inf(1)},
		},
		// Test case 5: NaN in lower bound
		{
			dst:  make([]float64, 5),
			l:    math.NaN(),
			u:    4,
			want: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 4},
		},
		// Test case 6: NaN in upper bound
		{
			dst:  make([]float64, 5),
			l:    0,
			u:    math.NaN(),
			want: []float64{0, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
		},
	}

	for _, tt := range tests {
		if tt.panics {
			func() {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("expected panic for dst=%v, l=%v, u=%v, but did not", tt.dst, tt.l, tt.u)
					}
				}()
				Span(tt.dst, tt.l, tt.u)
			}()
		} else {
			got := Span(tt.dst, tt.l, tt.u)
			if !equalFloatSlices(got, tt.want) {
				t.Errorf("Span(%v, %v, %v) = %v; want %v", tt.dst, tt.l, tt.u, got, tt.want)
			}
		}
	}
}

func equalFloatSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !floatEquals(a[i], b[i]) {
			return false
		}
	}
	return true
}

func floatEquals(a, b float64) bool {
	if math.IsNaN(a) && math.IsNaN(b) {
		return true
	}
	return a == b
}

