		{5, math.Inf(-1), math.Inf(1), 5, 2}, // Inf limits out of bounds
		{5, math.NaN(), 10, 5, 4}, // NaN lower bound
		{5, 0, math.NaN(), 5, 0}, // NaN upper bound
		{5, math.NaN(), math.NaN(), 5, 0}, // both bounds NaN
	}

	for _, test := range tests {
		t.Run("", func(t *testing.T) {
