func TestDistance(t *testing.T) {
	tests := []struct {
		s        []float64
		t        []float64
		L        float64
		expected float64
	}{
		// Test case for L=1 with positive numbers
		{[]float64{1, 2, 3}, []float64{4, 5, 6}, 1, 9},
		// Test case for L=2 with positive numbers
		{[]float64{1, 2, 3}, []float64{4, 5, 6}, 2, math.Sqrt(27)},
		// Test case for L=Inf using extreme values
		{[]float64{1, 2, 3}, []float64{4, 5, 6}, math.Inf(1), 3},
		// Test with negative numbers
		{[]float64{-1, -2, -3}, []float64{-4, -5, -6}, 1, 9},
		// Test with zero vectors
		{[]float64{0, 0, 0}, []float64{0, 0, 0}, 2, 0},
		// Test with empty vectors
		{[]float64{}, []float64{}, 1, 0},  // should return 0 if both are empty
	}

	for _, tt := range tests {
		t.Run("", func(t *testing.T) {
			defer func() {
				if r := recover(); r != nil {
					if len(tt.s) != len(tt.t) {
						// Expected panic due to unequal lengths
						return
					}
					t.Errorf("Distance panicked: %v", r)
				}
			}()
			result := Distance(tt.s, tt.t, tt.L)
			if !floatEqual(result, tt.expected) {
				t.Errorf("Distance(%v, %v, %v) = %v; expected %v", tt.s, tt.t, tt.L, result, tt.expected)
			}
		})
	}
}

// Helper function to compare floating point numbers for equality
func floatEqual(a, b float64) bool {
	const tol = 1e-9
	return math.Abs(a-b) < tol
}

