package floats

import (
	"errors"
	"testing"
)

func TestFind(t *testing.T) {
	tests := []struct {
		name     string
		inds     []int
		f        func(float64) bool
		s        []float64
		k        int
		expected []int
		err      error
	}{
		{
			name:     "no elements requested",
			inds:     []int{1, 2, 3},
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{1, 2, 3},
			k:        0,
			expected: []int{},
			err:      nil,
		},
		{
			name:     "k is negative, find all",
			inds:     []int{},
			f:        func(x float64) bool { return x > 1 },
			s:        []float64{1, 2, 3, 0, -1},
			k:        -1,
			expected: []int{1, 2},
			err:      nil,
		},
		{
			name:     "k is positive, found enough elements",
			inds:     []int{},
			f:        func(x float64) bool { return x%2 == 0 },
			s:        []float64{1, 2, 3, 4, 5},
			k:        2,
			expected: []int{1, 3},
			err:      nil,
		},
		{
			name:     "k is positive, not enough elements found",
			inds:     []int{},
			f:        func(x float64) bool { return x > 100 },
			s:        []float64{1, 2, 3},
			k:        1,
			expected: []int{},
			err:      errors.New("floats: insufficient elements found"),
		},
		{
			name:     "empty slice",
			inds:     []int{},
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{},
			k:        1,
			expected: []int{},
			err:      errors.New("floats: insufficient elements found"),
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := Find(tt.inds, tt.f, tt.s, tt.k)
			if !equalIntSlices(got, tt.expected) || (err != nil && err.Error() != tt.err.Error()) {
				t.Errorf("Find() = %v, %v; expected %v, %v", got, err, tt.expected, tt.err)
			}
		})
	}
}

func equalIntSlices(a, b []int) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

