)

func TestSpan(t *testing.T) {
	tests := []struct {
		dst        []float64
		l, u      float64
		expected   []float64
		expectPanic bool
	}{
		{
			dst:        make([]float64, 5),
			l:          0,
			u:          4,
			expected:   []float64{0, 1, 2, 3, 4},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 3),
			l:          -1,
			u:          1,
			expected:   []float64{-1, 0, 1},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 4),
			l:          1,
			u:          1,
			expected:   []float64{1, 1, 1, 1},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 10),
			l:          math.NaN(),
			u:          5,
			expected:   []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN(), 5},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 10),
			l:          5,
			u:          math.NaN(),
			expected:   []float64{5, math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN()},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 0),
			l:          0,
			u:          1,
			expected:   nil,
			expectPanic: true,
		},
		{
			dst:        make([]float64, 1),
			l:          0,
			u:          1,
			expected:   nil,
			expectPanic: true,
		},
	}

	for _, tt := range tests {
		if tt.expectPanic {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Expected panic but did not occur")
				}
			}()
		}

		result := Span(tt.dst, tt.l, tt.u)

		if !tt.expectPanic && !equalSlices(result, tt.expected) {
			t.Errorf("Span(%v, %v, %v) = %v; want %v", tt.dst, tt.l, tt.u, result, tt.expected)
		}
	}
}

// Helper function to compare slices for equality
func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !floatEqual(a[i], b[i]) {
			return false
		}
	}
	return true
}

// Helper function to compare float64 values for equality, considering NaNs
func floatEqual(a, b float64) bool {
	if math.IsNaN(a) && math.IsNaN(b) {
		return true
	}
	return a == b
}

