		// Test case 5: Case where lower bound is NaN
		{make([]float64, 5), math.NaN(), 5.0, []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 5.0}, false},
		// Test case 6: Case where upper bound is NaN
		{make([]float64, 5), 0.0, math.NaN(), []float64{0.0, math.NaN(), math.NaN(), math.NaN(), math.NaN()}, false},
		// Test case 7: Both bounds are Inf
		{make([]float64, 5), math.Inf(-1), math.Inf(1), []float64{math.Inf(-1), math.Inf(-1), math.Inf(1), math.Inf(1), math.NaN()}, false},
		// Test case 8: Lower bound is Inf
		{make([]float64, 5), math.Inf(1), 5.0, []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), 5.0}, false},
