package floats

import (
	"math"
	"testing"
)

func TestWithin(t *testing.T) {
	tests := []struct {
		s        []float64
		v        float64
		expected int
	}{
		// Test case where v is within the range of sorted s
		{[]float64{1, 2, 3, 4}, 2.5, 1},
		// Test case where v is exactly at the lower boundary
		{[]float64{1, 2, 3}, 1, 0},
		// Test case where v is exactly at the upper boundary
		{[]float64{1, 2, 3}, 2, 1},
		// Test case with a value less than the first element
		{[]float64{1, 2, 3}, 0.5, -1},
		// Test case with a value greater than or equal to the last element
		{[]float64{1, 2, 3}, 3, -1},
		// Test case with NaN value for v
		{[]float64{1, 2, 3}, math.NaN(), -1},
		// Test case with a single element slice (should panic)
	}

	for _, test := range tests {
		t.Run("", func(t *testing.T) {
			defer func() {
				if r := recover(); r != nil {
					// Check for panic on illegal input (len(s) < 2)
					if len(test.s) < 2 {
						return
					}
				}
			}()

			result := Within(test.s, test.v)
			if result != test.expected {
				t.Errorf("Within(%v, %v) = %d; expected %d", test.s, test.v, result, test.expected)
			}
		})
	}

	// Additional tests for panic cases
	assertPanic(t, func() { Within([]float64{1}) }, "panicked on single element slice")
	assertPanic(t, func() { Within([]float64{3, 1, 2}) }, "panicked on unsorted slice")
}

func assertPanic(t *testing.T, f func(), msg string) {
	defer func() {
		if r := recover(); r == nil {
			t.Errorf("expected panic for %s, but did not", msg)
		}
	}()
	f()
}

