package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		dst      []float64
		l        float64
		u        float64
		expected []float64
	}{
		{make([]float64, 5), 0, 4, []float64{0, 1, 2, 3, 4}},
		{make([]float64, 3), 2, 8, []float64{2, 5, 8}},
		{make([]float64, 7), -3, 3, []float64{-3, -2, -1, 0, 1, 2, 3}},
		{make([]float64, 4), 3, 3, []float64{3, 3, 3, 3}},
		{make([]float64, 2), 1, 5, []float64{1, 5}},
	}

	for _, test := range tests {
		got := Span(test.dst, test.l, test.u)
		if !equalSlices(got, test.expected) {
			t.Errorf("Span(%v, %f, %f) = %v; want %v", test.dst, test.l, test.u, got, test.expected)
		}
	}

	// Test panic for dst length less than 2
	defer func() {
		if r := recover(); r == nil {
			t.Errorf("Span did not panic for dst length less than 2")
		}
	}()
	Span(make([]float64, 1), 0, 1)

	// Test behaviors with NaN and Inf
	nan := math.NaN()
	infinity := math.Inf(1)

	testsSpecialCases := []struct {
		dst      []float64
		l        float64
		u        float64
		expected []float64
	}{
		{make([]float64, 5), nan, 4, []float64{nan, nan, nan, nan, 4}},
		{make([]float64, 5), 0, nan, []float64{0, nan, nan, nan, nan}},
		{make([]float64, 5), infinity, 4, []float64{infinity, infinity, infinity, infinity, 4}},
		{make([]float64, 5), -4, infinity, []float64{-4, -3, -2, -1, infinity}},
	}

	for _, test := range testsSpecialCases {
		got := Span(test.dst, test.l, test.u)
		if !equalSlices(got, test.expected) {
			t.Errorf("Span(%v, %f, %f) = %v; want %v", test.dst, test.l, test.u, got, test.expected)
		}
	}
}

// Helper function to compare slices for equality
func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !floatEqual(a[i], b[i]) {
			return false
		}
	}
	return true
}

// Helper function to compare float64 values for equality including NaN checks
func floatEqual(x, y float64) bool {
	if math.IsNaN(x) && math.IsNaN(y) {
		return true
	}
	return x == y
}

