			if !math.IsNaN(got[i]) {
				t.Errorf("Expected NaN at index %d; got %v", i, got[i])
			}
		}
		if got[len(got)-1] != 1 {
			t.Errorf("Expected %v at last index; got %v", 1, got[len(got)-1])
		}

		got = Span(dst, 1, math.NaN())
		for i := 1; i < len(got); i++ {
			if !math.IsNaN(got[i]) {
				t.Errorf("Expected NaN at index %d; got %v", i, got[i])
			}
