		dst := make([]float64, 5)
		got := Span(dst, math.Inf(-1), math.Inf(1))
		for i := 0; i < len(got)/2; i++ {
			if got[i] != math.Inf(-1) || got[len(got)-i-1] != math.Inf(1) {
				t.Errorf("Expected %v at index %d; got %v", math.Inf(-1), i, got[i])
