// span_test.go
package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		dst          []float64
		l, u        float64
		expected    []float64
		expectPanic bool
	}{
		{
			dst:       make([]float64, 3),
			l:         1,
			u:         3,
			expected:  []float64{1, 2, 3},
		},
		{
			dst:       make([]float64, 4),
			l:         0,
			u:         12,
			expected:  []float64{0, 4, 8, 12},
		},
		{
			dst:       make([]float64, 5),
			l:         -10,
			u:         10,
			expected:  []float64{-10, -5, 0, 5, 10},
		},
		{
			dst:       make([]float64, 2),
			l:         5,
			u:         5,
			expected:  []float64{5, 5},
		},
		{
			dst:          make([]float64, 1),
			l:            1,
			u:            2,
			expectPanic: true,
		},
		{
			dst:          make([]float64, 0),
			l:            1,
			u:            2,
			expectPanic: true,
		},
		{
			dst:       make([]float64, 3),
			l:         math.NaN(),
			u:         3,
			expected:  []float64{math.NaN(), math.NaN(), 3},
		},
		{
			dst:       make([]float64, 3),
			l:         1,
			u:         math.NaN(),
			expected:  []float64{1, math.NaN(), math.NaN()},
		},
		{
			dst:       make([]float64, 4),
			l:         math.Inf(1),
			u:         math.Inf(1),
			expected:  []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1)},
		},
		{
			dst:       make([]float64, 4),
			l:         math.Inf(-1),
			u:         math.Inf(-1),
			expected:  []float64{math.Inf(-1), math.Inf(-1), math.Inf(-1), math.Inf(-1)},
		},
		{
			dst:       make([]float64, 3),
			l:         math.Inf(-1),
			u:         math.Inf(1),
			expected:  []float64{math.Inf(-1), 0, math.Inf(1)},
		},
	}

	for _, test := range tests {
		if test.expectPanic {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Expected panic for input %+v, but did not panic", test)
				}
			}()
		}

		result := Span(test.dst, test.l, test.u)

		if test.expectPanic {
			// If we reached this point, we didn't panic, so we return.
			continue
		}

		if !equalSlices(result, test.expected) {
			t.Errorf("For input %+v, expected %+v, got %+v", test, test.expected, result)
		}
	}
}

func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !(math.IsNaN(a[i]) && math.IsNaN(b[i]) || a[i] == b[i]) {
			return false
		}
	}
	return true
}

