func TestSumCompensated(t *testing.T) {
	tests := []struct {
		slice   []float64
		want    float64
		wantErr bool
	}{
		{
			slice: []float64{1.0, 2.0, 3.0},
			want:  6.0,
		},
		{
			slice: []float64{1e20, 1e20, 1e-20},
			want:  2e20,
		},
		{
			slice: []float64{1e20, 1e20, -1e20},
			want:  1e20,
		},
		{
			slice: []float64{1.5, 2.5, 3.5},
			want:  7.5,
		},
		{
			slice: []float64{},
			want:  0.0,
		},
		{
			slice: []float64{math.NaN()},
			want:  math.NaN(),
		},
		{
			slice: []float64{math.Inf(1), math.Inf(1)},
			want:  math.Inf(1),
		},
	}

	for _, tt := range tests {
		t.Run("", func(t *testing.T) {
			got := SumCompensated(tt.slice)
			if math.IsNaN(got) && !math.IsNaN(tt.want) || !math.IsNaN(got) && math.IsNaN(tt.want) {
				t.Errorf("got %v, want %v", got, tt.want)
			} else if !math.IsNaN(got) && !math.IsNaN(tt.want) && !almostEqual(got, tt.want) {
				t.Errorf("got %v, want %v", got, tt.want)
			}
		})
	}
}

func almostEqual(a, b float64) bool {
	const tolerance = 1e-9
	return math.Abs(a-b) <= tolerance
}

