func TestAddTo(t *testing.T) {
	tests := []struct {
		name     string
		dst      []float64
		s        []float64
		t        []float64
		expected []float64
		wantPanic bool // Indicates if we expect a panic due to length mismatch
	}{
		{
			name:     "normal case",
			dst:      []float64{0, 0, 0},
			s:        []float64{1, 2, 3},
			t:        []float64{4, 5, 6},
			expected: []float64{5, 7, 9},
			wantPanic: false,
		},
		{
			name:     "length mismatch dst",
			dst:      []float64{0, 0},
			s:        []float64{1, 2, 3},
			t:        []float64{4, 5, 6},
			wantPanic: true,
		},
		{
			name:     "length mismatch s and t",
			dst:      []float64{0, 0, 0},
			s:        []float64{1, 2},
			t:        []float64{4, 5, 6},
			wantPanic: true,
		},
		{
			name:     "empty slices",
			dst:      []float64{},
			s:        []float64{},
			t:        []float64{},
			expected: []float64{},
			wantPanic: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			defer func() {
				if r := recover(); r != nil {
					if !tt.wantPanic {
						t.Errorf("AddTo() panicked unexpectedly: %v", r)
					}
				}
			}()

			result := AddTo(tt.dst, tt.s, tt.t)
			if tt.wantPanic {
				return
			}

			if !equalSlices(result, tt.expected) {
				t.Errorf("AddTo() = %v, want %v", result, tt.expected)
			}
		})
	}
}

// Helper function to compare two slices for equality
func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

