func TestAddTo(t *testing.T) {
	tests := []struct {
		dst     []float64
		s       []float64
		t       []float64
		want    []float64
		wantErr bool
	}{
		{
			dst:  []float64{0, 0, 0},
			s:    []float64{1, 2, 3},
			t:    []float64{4, 5, 6},
			want: []float64{5, 7, 9},
		},
		{
			dst:  []float64{0, 0},
			s:    []float64{1, 2},
			t:    []float64{3, 4},
			want: []float64{4, 6},
		},
		{
			dst:     []float64{0, 0, 0},
			s:       []float64{1, 2},
			t:       []float64{3, 4},
			wantErr: true,
		},
		{
			dst:     []float64{0},
			s:       []float64{1, 2, 3},
			t:       []float64{4, 5, 6},
			wantErr: true,
		},
		{
			dst:     []float64{0, 0},
			s:       []float64{1},
			t:       []float64{4},
			wantErr: true,
		},
	}

	for _, tt := range tests {
		if tt.wantErr {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Expected panic for inputs %v, %v, %v but did not get one", tt.dst, tt.s, tt.t)
				}
			}()
		}

		got := AddTo(tt.dst, tt.s, tt.t)
		if !mathEqual(got, tt.want) {
			t.Errorf("AddTo() = %v, want %v", got, tt.want)
		}
	}
}

// Helper function to compare two float64 slices for equality.
func mathEqual(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !floatEqual(a[i], b[i]) {
			return false
		}
	}
	return true
}

// Helper function to check if two float64 values are approximately equal.
func floatEqual(x, y float64) bool {
	return math.Abs(x-y) < 1e-9
}

