func TestSpan(t *testing.T) {
	tests := []struct {
		dst  []float64
		l    float64
		u    float64
		want []float64
	}{
		{
			dst:  make([]float64, 5),
			l:    0,
			u:    1,
			want: []float64{0, 0.25, 0.5, 0.75, 1},
		},
		{
			dst:  make([]float64, 3),
			l:    1,
			u:    5,
			want: []float64{1, 3, 5},
		},
		{
			dst:  make([]float64, 4),
			l:    -2,
			u:    2,
			want: []float64{-2, -1, 0, 2},
		},
		{
			dst:  make([]float64, 2),
			l:    1,
			u:    1,
			want: []float64{1, 1},
		},
	}

	for _, tt := range tests {
		got := Span(tt.dst, tt.l, tt.u)
		for i := range got {
			if got[i] != tt.want[i] {
				t.Errorf("Span(%v, %v, %v) = %v; want %v", tt.dst, tt.l, tt.u, got, tt.want)
				break
			}
		}
	}

	// Test panic for length less than 2
	defer func() {
		if r := recover(); r == nil {
			t.Errorf("Span() did not panic for dst length < 2")
		}
	}()
	Span(make([]float64, 1), 0, 1)

	// Test NaN cases
	dst := make([]float64, 5)
	Span(dst, math.NaN(), 1)
	for _, v := range dst[:len(dst)-1] {
		if !math.IsNaN(v) {
			t.Errorf("Expected NaN in dst with l being NaN; got %v", v)
		}
	}
	if dst[len(dst)-1] != 1 {
		t.Errorf("Expected last element to be 1; got %v", dst[len(dst)-1])
	}

	dst = make([]float64, 5)
	Span(dst, 0, math.NaN())
	for i := 1; i < len(dst); i++ {
		if !math.IsNaN(dst[i]) {
			t.Errorf("Expected NaN in dst with u being NaN; got %v", dst[i])
		}
	}
	if dst[0] != 0 {
		t.Errorf("Expected first element to be 0; got %v", dst[0])
	}

	// Test Inf cases
	dst = make([]float64, 5)
	Span(dst, math.Inf(-1), 1)
	for i, v := range dst[:len(dst)-1] {
		if v != math.Inf(-1) {
			t.Errorf("Expected %v at index %d; got %v", math.Inf(-1), i, v)
		}
	}
	if dst[len(dst)-1] != 1 {
		t.Errorf("Expected last element to be 1; got %v", dst[len(dst)-1])
	}

	dst = make([]float64, 5)
	Span(dst, 0, math.Inf(1))
	for i := 1; i < len(dst); i++ {
		if dst[i] != math.Inf(1) {
			t.Errorf("Expected %v at index %d; got %v", math.Inf(1), i, dst[i])
		}
	}
	if dst[0] != 0 {
		t.Errorf("Expected first element to be 0; got %v", dst[0])
	}
}

