package floats

import (
	"errors"
	"testing"
)

func TestFind(t *testing.T) {
	tests := []struct {
		name     string
		inds     []int
		f        func(float64) bool
		s        []float64
		k        int
		expected []int
		err      error
	}{
		{
			name:     "Find first 2 matching elements",
			inds:     []int{},
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{-1, 2, 3, -4, 5},
			k:        2,
			expected: []int{1, 2},
			err:      nil,
		},
		{
			name:     "Find all matching elements (k < 0)",
			inds:     []int{},
			f:        func(x float64) bool { return x < 0 },
			s:        []float64{-1, 2, -3, -4, 5},
			k:        -1,
			expected: []int{0, 2, 3},
			err:      nil,
		},
		{
			name:     "Not enough matching elements",
			inds:     []int{},
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{-1, -2, -3},
			k:        2,
			expected: []int{},
			err:      errors.New("floats: insufficient elements found"),
		},
		{
			name:     "Find zero elements requested",
			inds:     []int{},
			f:        func(x float64) bool { return x < 0 },
			s:        []float64{-1, 2, -3, -4, 5},
			k:        0,
			expected: []int{},
			err:      nil,
		},
		{
			name:     "Find first matching element",
			inds:     []int{},
			f:        func(x float64) bool { return x == 0 },
			s:        []float64{0, 1, 2, 3},
			k:        1,
			expected: []int{0},
			err:      nil,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result, err := Find(tt.inds, tt.f, tt.s, tt.k)
			if !errors.Is(err, tt.err) {
				t.Fatalf("expected error %v, got %v", tt.err, err)
			}
			if len(result) != len(tt.expected) {
				t.Fatalf("expected %v, got %v", tt.expected, result)
			}
			for i := range result {
				if result[i] != tt.expected[i] {
					t.Fatalf("expected %v, got %v", tt.expected, result)
				}
			}
		})
	}
}

