package floats

import (
	"errors"
	"testing"
)

func TestFind(t *testing.T) {
	tests := []struct {
		name      string
		inds      []int
		f         func(float64) bool
		s         []float64
		k         int
		expected  []int
		expectErr bool
	}{
		{
			name: "Find first k elements satisfying f",
			inds: nil,
			f: func(x float64) bool {
				return x > 2
			},
			s:        []float64{1, 2, 3, 4, 5},
			k:        2,
			expected: []int{2, 3},
		},
		{
			name: "Find all elements when k is negative",
			inds: nil,
			f: func(x float64) bool {
				return x < 3
			},
			s:        []float64{1, 2, 3, 4, 5},
			k:        -1,
			expected: []int{0, 1},
		},
		{
			name: "Not enough elements found",
			inds: nil,
			f: func(x float64) bool {
				return x > 5
			},
			s:        []float64{1, 2, 3, 4, 5},
			k:        1,
			expected: []int{},
			expectErr: true,
		},
		{
			name: "K is zero",
			inds: nil,
			f: func(x float64) bool {
				return x < 3
			},
			s:        []float64{1, 2, 3, 4, 5},
			k:        0,
			expected: []int{},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			actual, err := Find(tt.inds, tt.f, tt.s, tt.k)
			if (err != nil) != tt.expectErr {
				t.Errorf("expected error: %v, got: %v", tt.expectErr, err)
			}
			if !equalSlices(actual, tt.expected) {
				t.Errorf("expected: %v, got: %v", tt.expected, actual)
			}
		})
	}
}

// equalSlices checks if two slices are equal
func equalSlices(a, b []int) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

