func TestSpan(t *testing.T) {
	tests := []struct {
		dst     []float64
		l       float64
		u       float64
		want    []float64
		expectsPanic bool
	}{
		{
			dst:          make([]float64, 5),
			l:            0,
			u:            4,
			want:         []float64{0, 1, 2, 3, 4},
			expectsPanic: false,
		},
		{
			dst:          make([]float64, 3),
			l:            -3,
			u:            3,
			want:         []float64{-3, 0, 3},
			expectsPanic: false,
		},
		{
			dst:          make([]float64, 6),
			l:            2,
			u:            2,
			want:         []float64{2, 2, 2, 2, 2, 2},
			expectsPanic: false,
		},
		{
			dst:          make([]float64, 0),
			l:            1,
			u:            5,
			want:         nil,
			expectsPanic: true,
		},
		{
			dst:          make([]float64, 1),
			l:            1,
			u:            5,
			want:         nil,
			expectsPanic: true,
		},
		{
			dst:          make([]float64, 4),
			l:            math.NaN(),
			u:            3,
			want:         []float64{math.NaN(), math.NaN(), math.NaN(), 3},
			expectsPanic: false,
		},
		{
			dst:          make([]float64, 4),
			l:            1,
			u:            math.NaN(),
			want:         []float64{1, math.NaN(), math.NaN(), math.NaN()},
			expectsPanic: false,
		},
		{
			dst:          make([]float64, 4),
			l:            math.Inf(1),
			u:            math.Inf(1),
			want:         []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1)},
			expectsPanic: false,
		},
	}

	for _, tt := range tests {
		if tt.expectsPanic {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Span(%v, %v, %v) did not panic", tt.dst, tt.l, tt.u)
				}
			}()
		}

		got := Span(tt.dst, tt.l, tt.u)

		if !tt.expectsPanic {
			for i, v := range got {
				if !equalFloat(v, tt.want[i]) {
					t.Errorf("Span(%v, %v, %v) = %v; want %v", tt.dst, tt.l, tt.u, got, tt.want)
					break
				}
			}
		}
	}
}

func equalFloat(a, b float64) bool {
	return (math.IsNaN(a) && math.IsNaN(b)) || a == b
}

