package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		dst []float64
		l, u float64
		expected []float64
	}{
		{
			dst:     make([]float64, 5),
			l:       0,
			u:       10,
			expected: []float64{0, 2.5, 5, 7.5, 10},
		},
		{
			dst:     make([]float64, 3),
			l:       -1,
			u:       1,
			expected: []float64{-1, 0, 1},
		},
		{
			dst:     make([]float64, 2),
			l:       -5,
			u:       5,
			expected: []float64{-5, 5},
		},
		{
			dst:     make([]float64, 5),
			l:       math.NaN(),
			u:       10,
			expected: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 10},
		},
		{
			dst:     make([]float64, 5),
			l:       0,
			u:       math.NaN(),
			expected: []float64{0, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
		},
		{
			dst:     make([]float64, 5),
			l:       math.Inf(-1),
			u:       math.Inf(1),
			expected: []float64{math.Inf(-1), math.Inf(-1), math.Inf(-1), math.Inf(-1), math.Inf(1)},
		},
	}

	for _, test := range tests {
		result := Span(test.dst, test.l, test.u)
		for i := range result {
			if !isEqual(result[i], test.expected[i]) {
				t.Errorf("Span(%v, %f, %f) = %v; expected %v", test.dst, test.l, test.u, result, test.expected)
				break
			}
		}
	}

	// Test panic for invalid destination size
	defer func() {
		if recover() == nil {
			t.Errorf("Span did not panic for insufficient length of dst")
		}
	}()
	Span(make([]float64, 1), 0, 1)
}

func isEqual(a, b float64) bool {
	return (a == b) || (math.IsNaN(a) && math.IsNaN(b))
}

