package floats

import (
	"math"
	"testing"
)

func TestDistance(t *testing.T) {
	tests := []struct {
		s       []float64
		t       []float64
		L       float64
		expected float64
		panics   bool
	}{
		{
			s:       []float64{1, 2, 3},
			t:       []float64{4, 5, 6},
			L:       2,
			expected: math.Sqrt(27), // L2 distance
			panics:   false,
		},
		{
			s:       []float64{1, -2, 3},
			t:       []float64{4, -5, 6},
			L:       1,
			expected: 9, // L1 distance
			panics:   false,
		},
		{
			s:       []float64{1, 2},
			t:       []float64{1, 2},
			L:       math.Inf(1),
			expected: 0, // L-inf distance
			panics:   false,
		},
		{
			s:       []float64{},
			t:       []float64{},
			L:       1,
			expected: 0, // edge case, empty slices
			panics:   false,
		},
		{
			s:       []float64{1, 2, 3},
			t:       []float64{1, 2},
			L:       2,
			expected: 0, // Should panic due to length mismatch
			panics:   true,
		},
	}

	for _, test := range tests {
		if test.panics {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Expected panic for s:%v t:%v L:%v", test.s, test.t, test.L)
				}
			}()
		}
		result := Distance(test.s, test.t, test.L)
		if !test.panics && result != test.expected {
			t.Errorf("Distance(%v, %v, %v) = %v; expected %v", test.s, test.t, test.L, result, test.expected)
		}
	}
}

